import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useTranslation } from '../../hooks/useTranslation';
import { useLanguage } from '../../hooks/useLanguage';
import { ZODIAC_SIGNS } from '../../utils/constants';

const ZodiacGrid = ({ horoscopeData = {} }) => {
  const { t } = useTranslation();
  const { currentLanguage } = useLanguage();
  const navigate = useNavigate();

  // 调试信息
  console.log('ZodiacGrid rendering...');
  console.log('ZODIAC_SIGNS:', ZODIAC_SIGNS);
  console.log('horoscopeData:', horoscopeData);

  const handleZodiacClick = (sign) => {
    navigate(`/${currentLanguage}/horoscope/${sign}`);
  };

  // 简化的星级显示
  const renderStars = (rating) => {
    return '★'.repeat(rating) + '☆'.repeat(5 - rating);
  };

  return (
    <div className="w-full bg-gray-100 p-4">
      <h3 className="text-lg font-bold mb-4">星座网格调试</h3>
      <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-6 gap-4">
        {Object.entries(ZODIAC_SIGNS).map(([sign, data]) => {
          const rating = horoscopeData[sign]?.overall || Math.floor(Math.random() * 5) + 1;
          
          console.log(`渲染星座: ${sign}`, data);
          
          return (
            <div
              key={sign}
              onClick={() => handleZodiacClick(sign)}
              className="bg-white border-2 border-purple-200 rounded-lg p-3 cursor-pointer hover:border-purple-400 transition-all duration-200 text-center"
              style={{ minHeight: '150px' }}
            >
              <div className="text-4xl mb-2">{data.symbol}</div>
              <div className="text-sm font-bold text-gray-800 mb-1">
                {t(`zodiac.${sign}.name`) || sign}
              </div>
              <div className="text-xs text-gray-600 mb-2">{data.dates}</div>
              <div className="text-yellow-500 text-sm">
                {renderStars(rating)}
              </div>
              <div className="text-xs text-gray-500 mt-1">
                {rating}/5
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default ZodiacGrid;
