import React, { useState, useEffect } from 'react';
import { useParams, Link, useLocation } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import { useTheme } from '../../components/theme/ThemeProvider';
import Header from '../../components/common/Header';
import Footer from '../../components/common/Footer';
import ShareButton from '../../components/social/ShareButton';

function DailyHoroscopePage() {
  const { year, month, day } = useParams();
  const location = useLocation();
  const [dailyData, setDailyData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { theme } = useTheme();

  // 从URL路径中检测语言
  const language = location.pathname.startsWith('/zh') ? 'zh' : 'en';

  useEffect(() => {
    const loadDailyData = async () => {
      try {
        setLoading(true);
        const dateStr = `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`;
        const response = await fetch(`/data/daily/${dateStr}.json`);
        
        if (!response.ok) {
          throw new Error('Data not found');
        }
        
        const data = await response.json();
        setDailyData(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    if (year && month && day) {
      loadDailyData();
    }
  }, [year, month, day]);

  if (loading) {
    return (
      <div className={`min-h-screen ${theme.background} flex items-center justify-center`}>
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-white mx-auto mb-4"></div>
          <p className="text-white">{language === 'zh' ? '加载中...' : 'Loading...'}</p>
        </div>
      </div>
    );
  }

  if (error || !dailyData) {
    return (
      <div className={`min-h-screen ${theme.background} flex items-center justify-center`}>
        <div className="text-center text-white">
          <h2 className="text-2xl font-bold mb-4">
            {language === 'zh' ? '数据不可用' : 'Data Not Available'}
          </h2>
          <p className="mb-4">
            {language === 'zh' ? '抱歉，此日期的运势数据不可用。' : 'Sorry, horoscope data for this date is not available.'}
          </p>
          <Link 
            to={language === 'zh' ? '/zh' : '/'} 
            className="bg-white text-purple-600 px-6 py-2 rounded-full hover:bg-gray-100 transition-colors"
          >
            {language === 'zh' ? '返回首页' : 'Back to Home'}
          </Link>
        </div>
      </div>
    );
  }

  const content = dailyData[language] || dailyData.en;
  
  return (
    <div className={`min-h-screen ${theme.background}`}>
      <Helmet>
        <title>{content?.title || 'Daily Horoscope'}</title>
        <meta name="description" content={content?.description || 'Daily horoscope rankings'} />
        <meta name="keywords" content={content?.keywords || 'horoscope, zodiac, astrology'} />
        
        {/* Open Graph tags */}
        <meta property="og:title" content={content?.title || 'Daily Horoscope'} />
        <meta property="og:description" content={content?.description || 'Daily horoscope rankings'} />
        <meta property="og:type" content="article" />
        <meta property="og:url" content={window.location.href} />
        
        {/* Twitter Card tags */}
        <meta name="twitter:card" content="summary_large_image" />
        <meta name="twitter:title" content={content?.title || 'Daily Horoscope'} />
        <meta name="twitter:description" content={content?.description || 'Daily horoscope rankings'} />
        
        {/* hreflang tags */}
        <link rel="alternate" hrefLang="en" href={`/daily-horoscope/${year}/${month}/${day}`} />
        <link rel="alternate" hrefLang="zh" href={`/zh/daily-horoscope/${year}/${month}/${day}`} />
        
        {/* Structured Data */}
        <script type="application/ld+json">
          {JSON.stringify({
            "@context": "https://schema.org",
            "@type": "Article",
            "headline": content?.title,
            "description": content?.description,
            "datePublished": `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`,
            "author": {
              "@type": "Organization",
              "name": "Daily Horoscope Today"
            }
          })}
        </script>
      </Helmet>

      <Header />
      
      <main className="container mx-auto px-4 py-8">
        <div className="text-center mb-12">
          <h1 className="text-4xl md:text-5xl font-bold text-white mb-4">
            {language === 'zh' ? '今日星座排行榜' : "Today's Zodiac Rankings"}
          </h1>
          <p className="text-xl text-white/90 mb-6">
            {year}/{month}/{day}
          </p>
        </div>

        {content?.rankings && (
          <div className="max-w-4xl mx-auto">
            <div className="grid gap-6">
              {content.rankings.map((zodiac, index) => (
                <div 
                  key={zodiac.id || index}
                  className={`${theme.card} backdrop-blur-lg rounded-2xl p-6 transform hover:scale-105 transition-all duration-300`}
                >
                  <div className="flex items-center justify-between">
                    <div className="flex items-center space-x-4">
                      <div className="w-12 h-12 rounded-full bg-gradient-to-r from-orange-400 to-pink-500 flex items-center justify-center text-white font-bold text-lg">
                        #{zodiac.rank || index + 1}
                      </div>
                      <div>
                        <h3 className="text-xl font-bold text-white">
                          {zodiac.emoji} {zodiac.name?.[language] || zodiac.name?.en || zodiac.name}
                        </h3>
                        <p className="text-white/80 mt-1">
                          {zodiac.description || 'No description available'}
                        </p>
                      </div>
                    </div>
                    <div className="text-right">
                      <div className="text-2xl font-bold text-white mb-1">
                        {zodiac.score || 0}
                      </div>
                      <div className="text-sm text-white/60">
                        {language === 'zh' ? '运势指数' : 'Fortune Score'}
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        <div className="flex justify-center space-x-4 mt-8">
          <Link 
            to={language === 'zh' ? '/zh' : '/'}
            className="bg-white/20 backdrop-blur-lg text-white px-6 py-3 rounded-full hover:bg-white/30 transition-all duration-300"
          >
            {language === 'zh' ? '返回首页' : 'Back to Home'}
          </Link>
          
          <ShareButton 
            url={window.location.href}
            title={content?.title || 'Daily Horoscope'}
            description={content?.description || 'Daily horoscope rankings'}
          />
        </div>
      </main>

      <Footer />
    </div>
  );
}

export default DailyHoroscopePage;
