import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useTranslation } from '../../hooks/useTranslation';
import { useLanguage } from '../../hooks/useLanguage';
import { ZODIAC_SIGNS } from '../../utils/constants';
import StarRating from '../ui/StarRating';

const ZodiacGrid = ({ horoscopeData = {} }) => {
  const { t } = useTranslation();
  const { currentLanguage } = useLanguage();
  const navigate = useNavigate();

  // 添加调试信息
  console.log('ZodiacGrid - horoscopeData:', horoscopeData);
  console.log('ZodiacGrid - ZODIAC_SIGNS:', ZODIAC_SIGNS);
  console.log('ZodiacGrid - Object.entries(ZODIAC_SIGNS):', Object.entries(ZODIAC_SIGNS));

  const handleZodiacClick = (sign) => {
    navigate(`/${currentLanguage}/horoscope/${sign}`);
  };

  return (
    <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-6 gap-4 p-4">
      {Object.entries(ZODIAC_SIGNS).map(([sign, data]) => {
        const rating = horoscopeData[sign]?.overall || Math.floor(Math.random() * 5) + 1;
        
        console.log(`Rendering ${sign}:`, { sign, data, rating }); // 调试信息
        
        return (
          <div
            key={sign}
            onClick={() => handleZodiacClick(sign)}
            className="bg-white rounded-lg shadow-md p-4 cursor-pointer hover:shadow-lg transition-shadow duration-200 text-center min-h-[120px] flex flex-col justify-center"
          >
            <div className="text-3xl mb-2">{data.symbol}</div>
            <div className="text-sm font-medium text-gray-800 mb-1">
              {t(`zodiac.${sign}.name`)}
            </div>
            <div className="text-xs text-gray-500 mb-2">{data.dates}</div>
            <StarRating rating={rating} size="sm" />
          </div>
        );
      })}
    </div>
  );
};

export default ZodiacGrid;
