import React, { useState, useEffect } from 'react';
import { useTranslation } from '../hooks/useTranslation';
import { useLanguage } from '../hooks/useLanguage';
import { generateDailyHoroscopes } from '../services/horoscopeService';
import ZodiacGrid from '../components/horoscope/ZodiacGrid';
import HoroscopeCard from '../components/horoscope/HoroscopeCard';
import { ZODIAC_SIGNS } from '../utils/constants';

const HomePage = () => {
  const { t, currentLanguage } = useTranslation();
  const [dailyHoroscopes, setDailyHoroscopes] = useState({});
  const [featuredSign, setFeaturedSign] = useState('leo');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadDailyHoroscopes = async () => {
      try {
        const horoscopes = await generateDailyHoroscopes(new Date(), currentLanguage);
        setDailyHoroscopes(horoscopes);
        
        // 随机选择一个特色星座
        const signs = Object.keys(ZODIAC_SIGNS);
        const randomSign = signs[Math.floor(Math.random() * signs.length)];
        setFeaturedSign(randomSign);
      } catch (error) {
        console.error('Failed to load horoscopes:', error);
      } finally {
        setLoading(false);
      }
    };

    loadDailyHoroscopes();
  }, [currentLanguage]);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-600 mx-auto mb-4"></div>
          <p className="text-gray-600">{t('common.loading')}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-50 to-blue-50">
      {/* Hero Section */}
      <div className="bg-gradient-to-r from-purple-600 to-blue-600 text-white py-16">
        <div className="container mx-auto px-4 text-center">
          <h1 className="text-4xl md:text-6xl font-bold mb-4">
            {t('common.title')}
          </h1>
          <p className="text-xl md:text-2xl text-purple-200">
            {t('common.subtitle')}
          </p>
        </div>
      </div>

      <div className="container mx-auto px-4 py-8">
        {/* Featured Horoscope */}
        <div className="mb-12">
          <h2 className="text-2xl font-bold text-center mb-8 text-gray-800">
            🌟 {t('horoscope.todayHoroscope')}
          </h2>
          <div className="max-w-md mx-auto">
            <HoroscopeCard 
              zodiacSign={featuredSign} 
              horoscope={dailyHoroscopes[featuredSign]} 
            />
          </div>
        </div>

        {/* Zodiac Grid */}
        <div className="mb-12">
          <h2 className="text-2xl font-bold text-center mb-8 text-gray-800">
            🔮 {t('horoscope.selectZodiac')}
          </h2>
          <ZodiacGrid horoscopeData={dailyHoroscopes} />
        </div>

        {/* Top Rankings */}
        <div className="bg-white rounded-xl shadow-lg p-6">
          <h2 className="text-2xl font-bold text-center mb-6 text-gray-800">
            📊 {t('horoscope.topRanking')}
          </h2>
          <div className="space-y-3">
            {Object.entries(dailyHoroscopes)
              .sort(([,a], [,b]) => (b?.overall || 0) - (a?.overall || 0))
              .slice(0, 5)
              .map(([sign, data], index) => (
                <div key={sign} className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
                  <div className="flex items-center">
                    <span className="text-2xl mr-3">
                      {index === 0 ? '🥇' : index === 1 ? '🥈' : index === 2 ? '🥉' : `${index + 1}.`}
                    </span>
                    <span className="text-2xl mr-3">{ZODIAC_SIGNS[sign]?.symbol}</span>
                    <span className="font-medium">{t(`zodiac.${sign}.name`)}</span>
                  </div>
                  <div className="flex items-center">
                    <span className="text-sm text-gray-600 mr-2">{data?.overall || 0}/5</span>
                    <div className="flex">
                      {[...Array(5)].map((_, i) => (
                        <span key={i} className={`text-sm ${
                          i < (data?.overall || 0) ? 'text-yellow-400' : 'text-gray-300'
                        }`}>★</span>
                      ))}
                    </div>
                  </div>
                </div>
              ))
            }
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
