import React, { useState, useEffect } from 'react';
import { generateDailyHoroscopes } from '../../services/horoscopeService';
import ZodiacGrid from '../../components/horoscope/ZodiacGrid';
import HoroscopeCard from '../../components/horoscope/HoroscopeCard';
import { ZODIAC_SIGNS } from '../../utils/constants';
import UserProfile from '../../components/user/UserProfile';
import ShareButton from '../../components/social/ShareButton';
import HoroscopeChart from '../../components/charts/HoroscopeChart';
import ThemeSelector from '../../components/theme/ThemeSelector';
import { useTheme } from '../../components/theme/ThemeProvider';
import '../../styles/animations.css';
import { Link } from 'react-router-dom';

const BaseHomePage = ({ language, content }) => {
  const { theme } = useTheme();
  const [dailyHoroscopes, setDailyHoroscopes] = useState({});
  const [featuredSign, setFeaturedSign] = useState('leo');
  const [loading, setLoading] = useState(true);

  // ÂàõÂª∫ÈªòËÆ§ÂÜÖÂÆπÂØπË±°
  const defaultContent = {
    zodiacNames: {
      aries: language === 'zh' ? 'ÁôΩÁæäÂ∫ß' : 'Aries',
      taurus: language === 'zh' ? 'ÈáëÁâõÂ∫ß' : 'Taurus',
      gemini: language === 'zh' ? 'ÂèåÂ≠êÂ∫ß' : 'Gemini',
      cancer: language === 'zh' ? 'Â∑®ËüπÂ∫ß' : 'Cancer',
      leo: language === 'zh' ? 'ÁãÆÂ≠êÂ∫ß' : 'Leo',
      virgo: language === 'zh' ? 'Â§ÑÂ•≥Â∫ß' : 'Virgo',
      libra: language === 'zh' ? 'Â§©Áß§Â∫ß' : 'Libra',
      scorpio: language === 'zh' ? 'Â§©ËùéÂ∫ß' : 'Scorpio',
      sagittarius: language === 'zh' ? 'Â∞ÑÊâãÂ∫ß' : 'Sagittarius',
      capricorn: language === 'zh' ? 'Êë©ÁæØÂ∫ß' : 'Capricorn',
      aquarius: language === 'zh' ? 'Ê∞¥Áì∂Â∫ß' : 'Aquarius',
      pisces: language === 'zh' ? 'ÂèåÈ±ºÂ∫ß' : 'Pisces'
    },
    categories: {
      overall: language === 'zh' ? 'ÁªºÂêà' : 'Overall',
      love: language === 'zh' ? 'Áà±ÊÉÖ' : 'Love',
      career: language === 'zh' ? '‰∫ã‰∏ö' : 'Career',
      money: language === 'zh' ? 'Ë¥¢Ëøê' : 'Money',
      health: language === 'zh' ? 'ÂÅ•Â∫∑' : 'Health'
    },
    radarChart: language === 'zh' ? 'ËøêÂäøÈõ∑ËææÂõæ' : 'Fortune Radar Chart',
    profileCenter: language === 'zh' ? '‰∏™‰∫∫‰∏≠ÂøÉ' : 'Profile Center',
    name: language === 'zh' ? 'ÂßìÂêç' : 'Name',
    birthday: language === 'zh' ? 'ÁîüÊó•' : 'Birthday',
    zodiacSign: language === 'zh' ? 'ÊòüÂ∫ß' : 'Zodiac Sign',
    favoriteColor: language === 'zh' ? 'ÂñúÊ¨¢ÁöÑÈ¢úËâ≤' : 'Favorite Color',
    enterName: language === 'zh' ? 'ËØ∑ËæìÂÖ•ÊÇ®ÁöÑÂßìÂêç' : 'Enter your name'
  };

  // ÂêàÂπ∂ÈªòËÆ§ÂÜÖÂÆπÂíå‰º†ÂÖ•ÁöÑÂÜÖÂÆπ
  const finalContent = { ...defaultContent, ...content };

  useEffect(() => {
    const loadDailyHoroscopes = async () => {
      try {
        const horoscopes = await generateDailyHoroscopes(new Date(), language);
        setDailyHoroscopes(horoscopes);
        
        const signs = Object.keys(ZODIAC_SIGNS);
        const randomSign = signs[Math.floor(Math.random() * signs.length)];
        setFeaturedSign(randomSign);
      } catch (error) {
        console.error('Failed to load horoscopes:', error);
      } finally {
        setLoading(false);
      }
    };

    loadDailyHoroscopes();
  }, [language]);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-600 mx-auto mb-4"></div>
          <p className={`${theme.colors.textSecondary}`}>{content.loading}</p>
        </div>
      </div>
    );
  }

  return (
    <div className={`min-h-screen ${theme.colors.background} transition-colors duration-300`}>
      {/* Hero Section */}
      <section className="relative overflow-hidden py-20 px-4">
        <div className="absolute inset-0 bg-gradient-to-br from-purple-600 via-blue-600 to-indigo-700"></div>
        <div className="absolute inset-0 bg-black opacity-20"></div>
        
        <div className="relative max-w-6xl mx-auto text-center text-white">
          <h1 className="text-5xl md:text-7xl font-bold mb-6 animate-fade-in">
            {content.title}
          </h1>
          <p className="text-xl md:text-2xl mb-8 opacity-90 animate-slide-up">
            {content.subtitle}
          </p>
          
          <div className="flex justify-center gap-4 mb-8">
            <ThemeSelector />
            <ShareButton 
              content={finalContent}
              url={window.location.href}
              title={content.title}
              description={content.subtitle}
            />
          </div>
        </div>
      </section>

      {/* Featured Horoscope */}
      {dailyHoroscopes[featuredSign] && (
        <section className="py-16 px-4">
          <div className="max-w-4xl mx-auto">
            <h2 className={`text-3xl font-bold text-center mb-12 ${theme.colors.textPrimary}`}>
              {content.todaysFortune}
            </h2>
            <HoroscopeCard 
              sign={featuredSign}
              horoscope={dailyHoroscopes[featuredSign]}
              content={finalContent}
              featured={true}
              language={language}
            />
          </div>
        </section>
      )}

      {/* Zodiac Grid */}
      <section className="py-16 px-4">
        <div className="max-w-6xl mx-auto">
          <h2 className={`text-3xl font-bold text-center mb-12 ${theme.colors.textPrimary}`}>
            {content.allSigns}
          </h2>
          <ZodiacGrid 
            horoscopes={dailyHoroscopes}
            content={finalContent}
            language={language}
          />
        </div>
      </section>

      {/* Horoscope Chart */}
      <section className="py-16 px-4">
        <div className="max-w-4xl mx-auto">
          <h2 className={`text-3xl font-bold text-center mb-12 ${theme.colors.textPrimary}`}>
            {content.weeklyTrend}
          </h2>
          <HoroscopeChart 
            content={finalContent}
            language={language} 
          />
        </div>
      </section>

      {/* User Profile */}
      <section className="py-16 px-4">
        <div className="max-w-4xl mx-auto">
          <UserProfile 
            content={finalContent}
            language={language} 
          />
        </div>
      </section>
    </div>
  );
};

export default BaseHomePage;

// Âú®ÁªÑ‰ª∂‰∏≠Ê∑ªÂä†ÊØèÊó•ËøêÂäøÈìæÊé•
const today = new Date();
const todayPath = `${language === 'zh' ? '/zh' : ''}/daily-horoscope/${today.getFullYear()}/${today.getMonth() + 1}/${today.getDate()}`;

// Âú®ÈÄÇÂΩì‰ΩçÁΩÆÊ∑ªÂä†ÈìæÊé•
<Link 
  to={todayPath}
  className="bg-gradient-to-r from-yellow-400 to-orange-500 text-white px-6 py-3 rounded-full font-semibold hover:shadow-lg transform hover:scale-105 transition-all duration-300"
>
  {language === 'zh' ? 'üåü ‰ªäÊó•ËøêÂäøÊéíË°åÊ¶ú' : 'üåü Today\'s Rankings'}
</Link>
