import React, { useState, useEffect } from 'react';
import { useParams, Link, useLocation } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import { useTheme } from '../../components/theme/ThemeProvider';
import Header from '../../components/common/Header';
import Footer from '../../components/common/Footer';
import ShareButton from '../../components/social/ShareButton';

function DailyHoroscopePage() {
  const { year, month, day } = useParams();
  const location = useLocation();
  const [dailyData, setDailyData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { theme } = useTheme();

  // 从URL路径中检测语言
  const language = location.pathname.startsWith('/zh') ? 'zh' : 'en';

  useEffect(() => {
    const loadDailyData = async () => {
      try {
        setLoading(true);
        const dateStr = `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`;
        const response = await fetch(`/data/daily/${dateStr}.json`);
        
        if (!response.ok) {
          throw new Error('Data not found');
        }
        
        const data = await response.json();
        setDailyData(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    if (year && month && day) {
      loadDailyData();
    }
  }, [year, month, day]);

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-pink-900 flex items-center justify-center">
        <div className="text-center bg-white/10 backdrop-blur-lg rounded-3xl p-8 shadow-2xl">
          <div className="animate-spin rounded-full h-16 w-16 border-4 border-white/30 border-t-white mx-auto mb-6"></div>
          <p className="text-white text-xl font-medium">{language === 'zh' ? '正在加载运势数据...' : 'Loading horoscope data...'}</p>
        </div>
      </div>
    );
  }

  if (error || !dailyData) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-pink-900 flex items-center justify-center">
        <div className="text-center bg-white/10 backdrop-blur-lg rounded-3xl p-8 shadow-2xl max-w-md mx-4">
          <div className="text-6xl mb-6">😔</div>
          <h2 className="text-3xl font-bold text-white mb-4">
            {language === 'zh' ? '数据暂时不可用' : 'Data Not Available'}
          </h2>
          <p className="text-white/80 text-lg mb-6">
            {language === 'zh' ? '抱歉，此日期的运势数据暂时无法获取。' : 'Sorry, horoscope data for this date is temporarily unavailable.'}
          </p>
          <Link 
            to={language === 'zh' ? '/zh' : '/'} 
            className="inline-block bg-gradient-to-r from-orange-400 to-pink-500 text-white font-semibold px-8 py-3 rounded-full hover:from-orange-500 hover:to-pink-600 transform hover:scale-105 transition-all duration-300 shadow-lg"
          >
            {language === 'zh' ? '返回首页' : 'Back to Home'}
          </Link>
        </div>
      </div>
    );
  }

  const content = dailyData[language] || dailyData.en;
  
  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-pink-900">
      <Helmet>
        <title>{content?.title || 'Daily Horoscope'}</title>
        <meta name="description" content={content?.description || 'Daily horoscope rankings'} />
        <meta name="keywords" content={content?.keywords || 'horoscope, zodiac, astrology'} />
        
        {/* Open Graph tags */}
        <meta property="og:title" content={content?.title || 'Daily Horoscope'} />
        <meta property="og:description" content={content?.description || 'Daily horoscope rankings'} />
        <meta property="og:type" content="article" />
        <meta property="og:url" content={window.location.href} />
        
        {/* Twitter Card tags */}
        <meta name="twitter:card" content="summary_large_image" />
        <meta name="twitter:title" content={content?.title || 'Daily Horoscope'} />
        <meta name="twitter:description" content={content?.description || 'Daily horoscope rankings'} />
        
        {/* hreflang tags */}
        <link rel="alternate" hrefLang="en" href={`/daily-horoscope/${year}/${month}/${day}`} />
        <link rel="alternate" hrefLang="zh" href={`/zh/daily-horoscope/${year}/${month}/${day}`} />
        
        {/* Structured Data */}
        <script type="application/ld+json">
          {JSON.stringify({
            "@context": "https://schema.org",
            "@type": "Article",
            "headline": content?.title,
            "description": content?.description,
            "datePublished": `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`,
            "author": {
              "@type": "Organization",
              "name": "Daily Horoscope Today"
            }
          })}
        </script>
      </Helmet>

      <Header />
      
      {/* Hero Section */}
      <div className="relative overflow-hidden py-16">
        <div className="absolute inset-0 bg-gradient-to-r from-yellow-400/20 via-orange-500/20 to-pink-500/20"></div>
        <div className="relative container mx-auto px-4 text-center">
          <div className="inline-block bg-white/10 backdrop-blur-lg rounded-2xl px-8 py-4 mb-6">
            <p className="text-orange-200 text-lg font-medium">
              {year}年{month}月{day}日
            </p>
          </div>
          <h1 className="text-5xl md:text-7xl font-bold text-white mb-6 leading-tight">
            <span className="bg-gradient-to-r from-yellow-300 via-orange-300 to-pink-300 bg-clip-text text-transparent">
              {language === 'zh' ? '今日星座' : "Today's Zodiac"}
            </span>
            <br />
            <span className="text-white">
              {language === 'zh' ? '运势排行榜' : 'Rankings'}
            </span>
          </h1>
          <p className="text-xl text-white/90 max-w-2xl mx-auto leading-relaxed">
            {language === 'zh' 
              ? '探索十二星座的今日运势，发现属于你的幸运时刻' 
              : 'Explore today\'s fortune for all zodiac signs and discover your lucky moments'
            }
          </p>
        </div>
      </div>

      {/* Rankings Section */}
      <main className="container mx-auto px-4 pb-16">
        {content?.rankings && (
          <div className="max-w-5xl mx-auto">
            <div className="grid gap-6">
              {content.rankings.map((zodiac, index) => {
                const isTopThree = (zodiac.rank || index + 1) <= 3;
                const rankColors = {
                  1: 'from-yellow-400 to-yellow-600',
                  2: 'from-gray-300 to-gray-500', 
                  3: 'from-orange-400 to-orange-600'
                };
                
                return (
                  <div 
                    key={zodiac.id || index}
                    className={`relative group ${
                      isTopThree 
                        ? 'bg-gradient-to-r from-white/20 to-white/10' 
                        : 'bg-white/10'
                    } backdrop-blur-lg rounded-3xl p-6 md:p-8 transform hover:scale-[1.02] transition-all duration-300 shadow-xl hover:shadow-2xl border border-white/20`}
                  >
                    {/* Top 3 Crown Effect */}
                    {isTopThree && (
                      <div className="absolute -top-3 left-1/2 transform -translate-x-1/2">
                        <div className="bg-gradient-to-r from-yellow-400 to-orange-500 text-white px-4 py-1 rounded-full text-sm font-bold shadow-lg">
                          {language === 'zh' ? '今日推荐' : 'Top Pick'}
                        </div>
                      </div>
                    )}
                    
                    <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4">
                      <div className="flex items-center space-x-6">
                        {/* Rank Badge */}
                        <div className={`w-16 h-16 md:w-20 md:h-20 rounded-full bg-gradient-to-r ${
                          rankColors[zodiac.rank] || 'from-purple-400 to-pink-500'
                        } flex items-center justify-center shadow-lg`}>
                          <span className="text-white font-bold text-xl md:text-2xl">
                            #{zodiac.rank || index + 1}
                          </span>
                        </div>
                        
                        {/* Zodiac Info */}
                        <div className="flex-1">
                          <div className="flex items-center gap-3 mb-2">
                            <span className="text-4xl md:text-5xl">{zodiac.emoji}</span>
                            <h3 className="text-2xl md:text-3xl font-bold text-white">
                              {zodiac.name?.[language] || zodiac.name?.en || zodiac.name}
                            </h3>
                          </div>
                          <p className="text-white/90 text-lg md:text-xl leading-relaxed max-w-2xl">
                            {zodiac.description || (language === 'zh' ? '暂无描述' : 'No description available')}
                          </p>
                        </div>
                      </div>
                      
                      {/* Score Section */}
                      <div className="text-center md:text-right bg-white/10 rounded-2xl p-4 md:p-6 min-w-[120px]">
                        <div className="text-4xl md:text-5xl font-bold text-white mb-2">
                          {zodiac.score || 0}
                        </div>
                        <div className="text-white/70 text-sm md:text-base font-medium">
                          {language === 'zh' ? '运势指数' : 'Fortune Score'}
                        </div>
                        <div className="mt-2">
                          <div className="w-full bg-white/20 rounded-full h-2">
                            <div 
                              className="bg-gradient-to-r from-yellow-400 to-orange-500 h-2 rounded-full transition-all duration-1000"
                              style={{ width: `${Math.min((zodiac.score || 0), 100)}%` }}
                            ></div>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        )}

        {/* Action Buttons */}
        <div className="flex flex-col sm:flex-row justify-center items-center gap-4 mt-12">
          <Link 
            to={language === 'zh' ? '/zh' : '/'}
            className="bg-white/20 backdrop-blur-lg text-white font-semibold px-8 py-4 rounded-full hover:bg-white/30 transition-all duration-300 shadow-lg hover:shadow-xl transform hover:scale-105 border border-white/30"
          >
            <span className="mr-2">🏠</span>
            {language === 'zh' ? '返回首页' : 'Back to Home'}
          </Link>
          
          <ShareButton 
            url={window.location.href}
            title={content?.title || 'Daily Horoscope'}
            description={content?.description || 'Daily horoscope rankings'}
          />
        </div>

        {/* Additional Info */}
        <div className="mt-16 text-center">
          <div className="bg-white/10 backdrop-blur-lg rounded-2xl p-6 max-w-2xl mx-auto border border-white/20">
            <h3 className="text-xl font-bold text-white mb-3">
              {language === 'zh' ? '💫 运势说明' : '💫 Fortune Guide'}
            </h3>
            <p className="text-white/80 leading-relaxed">
              {language === 'zh' 
                ? '运势指数基于星象位置、行星运行轨迹等天文数据计算得出，仅供娱乐参考。每日运势会根据天体变化实时更新。'
                : 'Fortune scores are calculated based on astronomical data including star positions and planetary movements. For entertainment purposes only. Daily horoscopes are updated in real-time based on celestial changes.'
              }
            </p>
          </div>
        </div>
      </main>

      <Footer />
    </div>
  );
}

export default DailyHoroscopePage;
