import React, { useState, useEffect } from 'react';
import { useTranslation } from '../hooks/useTranslation';
import { useLanguage } from '../hooks/useLanguage';
import { generateDailyHoroscopes } from '../services/horoscopeService';
import ZodiacGrid from '../components/horoscope/ZodiacGrid';
import HoroscopeCard from '../components/horoscope/HoroscopeCard';
import { ZODIAC_SIGNS } from '../utils/constants';
import UserProfile from '../components/user/UserProfile';
import ShareButton from '../components/social/ShareButton';
import HoroscopeChart from '../components/charts/HoroscopeChart';
import ThemeSelector from '../components/theme/ThemeSelector';
import { useTheme } from '../components/theme/ThemeProvider';
import '../styles/animations.css';

const HomePage = () => {
  const { t, currentLanguage } = useTranslation();
  const { theme } = useTheme();
  const [dailyHoroscopes, setDailyHoroscopes] = useState({});
  const [featuredSign, setFeaturedSign] = useState('leo');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadDailyHoroscopes = async () => {
      try {
        const horoscopes = await generateDailyHoroscopes(new Date(), currentLanguage);
        setDailyHoroscopes(horoscopes);
        
        // 随机选择一个特色星座
        const signs = Object.keys(ZODIAC_SIGNS);
        const randomSign = signs[Math.floor(Math.random() * signs.length)];
        setFeaturedSign(randomSign);
      } catch (error) {
        console.error('Failed to load horoscopes:', error);
      } finally {
        setLoading(false);
      }
    };

    loadDailyHoroscopes();
  }, [currentLanguage]);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-purple-600 mx-auto mb-4"></div>
          <p className={`${theme.colors.textSecondary}`}>{t('common.loading')}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="animate-fadeIn">
      {/* 主题切换器 - 固定在右上角 */}
      <div className="fixed top-4 right-4 z-50">
        <ThemeSelector />
      </div>

      {/* Hero Section */}
      <div className={`bg-gradient-to-r ${theme.colors.primary} text-white py-16 animate-slideDown`}>
        <div className="container mx-auto px-4 text-center">
          <h1 className="text-4xl md:text-6xl font-bold mb-4 animate-glow">
            {t('common.title')}
          </h1>
          <p className="text-xl md:text-2xl text-purple-200">
            {t('common.subtitle')}
          </p>
        </div>
      </div>

      <div className="container mx-auto px-4 py-8">
        {/* 个人运势中心 */}
        <div className="mb-12 animate-slideUp">
          <UserProfile />
        </div>

        {/* Featured Horoscope */}
        <div className="mb-12 animate-slideUp">
          <h2 className={`text-2xl font-bold text-center mb-8 ${theme.colors.text}`}>
            🌟 {t('horoscope.todayHoroscope')}
          </h2>
          <div className="max-w-md mx-auto relative">
            <HoroscopeCard 
              zodiacSign={featuredSign} 
              horoscope={dailyHoroscopes[featuredSign]} 
            />
            {/* 分享按钮 */}
            <div className="mt-4 text-center">
              <ShareButton 
                horoscope={dailyHoroscopes[featuredSign]}
                zodiacSign={featuredSign}
              />
            </div>
          </div>
        </div>

        {/* 运势图表 */}
        {dailyHoroscopes[featuredSign] && (
          <div className="mb-12 animate-slideUp">
            <h2 className={`text-2xl font-bold text-center mb-8 ${theme.colors.text}`}>
              📈 {t('horoscope.chart')}
            </h2>
            <div className="max-w-2xl mx-auto">
              <HoroscopeChart horoscope={dailyHoroscopes[featuredSign]} />
            </div>
          </div>
        )}

        {/* Zodiac Grid */}
        <div className="mb-12 animate-slideUp">
          <h2 className={`text-2xl font-bold text-center mb-8 ${theme.colors.text}`}>
            🔮 {t('horoscope.selectZodiac')}
          </h2>
          <ZodiacGrid horoscopeData={dailyHoroscopes} />
        </div>

        {/* Top Rankings */}
        <div className={`${theme.colors.card} rounded-xl shadow-lg p-6 animate-slideUp`}>
          <h2 className={`text-2xl font-bold text-center mb-6 ${theme.colors.text}`}>
            📊 {t('horoscope.topRanking')}
          </h2>
          <div className="space-y-3">
            {Object.entries(dailyHoroscopes)
              .sort(([,a], [,b]) => (b?.overall || 0) - (a?.overall || 0))
              .slice(0, 5)
              .map(([sign, data], index) => (
                <div key={sign} className={`flex items-center justify-between p-3 bg-gray-50 dark:bg-gray-700 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-600 transition-colors`}>
                  <div className="flex items-center">
                    <span className="text-2xl mr-3">
                      {index === 0 ? '🥇' : index === 1 ? '🥈' : index === 2 ? '🥉' : `${index + 1}.`}
                    </span>
                    <span className="text-2xl mr-3">{ZODIAC_SIGNS[sign]?.symbol}</span>
                    <span className={`font-medium ${theme.colors.text}`}>{t(`zodiac.${sign}.name`)}</span>
                  </div>
                  <div className="flex items-center">
                    <span className={`text-sm mr-2 ${theme.colors.textSecondary}`}>{data?.overall || 0}/5</span>
                    <div className="flex">
                      {[...Array(5)].map((_, i) => (
                        <span key={i} className={`text-sm ${
                          i < (data?.overall || 0) ? 'text-yellow-400' : 'text-gray-300'
                        }`}>★</span>
                      ))}
                    </div>
                  </div>
                </div>
              ))
            }
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
