import React, { useState, useEffect } from 'react';
import { useTranslation } from '../../hooks/useTranslation';
import { ZODIAC_SIGNS } from '../../utils/constants';

const UserProfile = () => {
  const { t } = useTranslation();
  const [userInfo, setUserInfo] = useState({
    name: '',
    birthday: '',
    zodiacSign: '',
    favoriteColor: '',
    notifications: true
  });

  useEffect(() => {
    // ä»ŽlocalStorageåŠ è½½ç”¨æˆ·ä¿¡æ¯
    const savedInfo = localStorage.getItem('userProfile');
    if (savedInfo) {
      setUserInfo(JSON.parse(savedInfo));
    }
  }, []);

  const calculateZodiacSign = (birthday) => {
    const date = new Date(birthday);
    const month = date.getMonth() + 1;
    const day = date.getDate();
    
    // æ˜Ÿåº§æ—¥æœŸåˆ¤æ–­é€»è¾‘
    if ((month === 3 && day >= 21) || (month === 4 && day <= 19)) return 'aries';
    if ((month === 4 && day >= 20) || (month === 5 && day <= 20)) return 'taurus';
    if ((month === 5 && day >= 21) || (month === 6 && day <= 20)) return 'gemini';
    if ((month === 6 && day >= 21) || (month === 7 && day <= 22)) return 'cancer';
    if ((month === 7 && day >= 23) || (month === 8 && day <= 22)) return 'leo';
    if ((month === 8 && day >= 23) || (month === 9 && day <= 22)) return 'virgo';
    if ((month === 9 && day >= 23) || (month === 10 && day <= 22)) return 'libra';
    if ((month === 10 && day >= 23) || (month === 11 && day <= 21)) return 'scorpio';
    if ((month === 11 && day >= 22) || (month === 12 && day <= 21)) return 'sagittarius';
    if ((month === 12 && day >= 22) || (month === 1 && day <= 19)) return 'capricorn';
    if ((month === 1 && day >= 20) || (month === 2 && day <= 18)) return 'aquarius';
    if ((month === 2 && day >= 19) || (month === 3 && day <= 20)) return 'pisces';
    return 'leo';
  };

  const handleBirthdayChange = (birthday) => {
    const zodiacSign = calculateZodiacSign(birthday);
    const updatedInfo = { ...userInfo, birthday, zodiacSign };
    setUserInfo(updatedInfo);
    localStorage.setItem('userProfile', JSON.stringify(updatedInfo));
  };

  const handleSave = () => {
    localStorage.setItem('userProfile', JSON.stringify(userInfo));
    alert(t('user.profileSaved'));
  };

  return (
    <div className="bg-white rounded-xl shadow-lg p-6 max-w-md mx-auto">
      <h2 className="text-2xl font-bold text-center mb-6 text-gray-800">
        ðŸ‘¤ {t('user.profileCenter')}
      </h2>
      
      <div className="space-y-4">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            {t('user.name')}
          </label>
          <input
            type="text"
            value={userInfo.name}
            onChange={(e) => setUserInfo({...userInfo, name: e.target.value})}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
            placeholder={t('user.namePlaceholder')}
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            {t('user.birthday')}
          </label>
          <input
            type="date"
            value={userInfo.birthday}
            onChange={(e) => handleBirthdayChange(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
          />
        </div>

        {userInfo.zodiacSign && (
          <div className="bg-purple-50 p-4 rounded-lg">
            <div className="flex items-center justify-center">
              <span className="text-4xl mr-3">{ZODIAC_SIGNS[userInfo.zodiacSign]?.symbol}</span>
              <div>
                <p className="font-medium text-purple-800">
                  {t('user.yourZodiac')}ï¼š{t(`zodiac.${userInfo.zodiacSign}.name`)}
                </p>
                <p className="text-sm text-purple-600">
                  {ZODIAC_SIGNS[userInfo.zodiacSign]?.dateRange}
                </p>
              </div>
            </div>
          </div>
        )}

        <div className="flex items-center">
          <input
            type="checkbox"
            id="notifications"
            checked={userInfo.notifications}
            onChange={(e) => setUserInfo({...userInfo, notifications: e.target.checked})}
            className="mr-2"
          />
          <label htmlFor="notifications" className="text-sm text-gray-700">
            {t('user.notifications')}
          </label>
        </div>

        <button
          onClick={handleSave}
          className="w-full bg-gradient-to-r from-purple-600 to-blue-600 text-white py-2 px-4 rounded-lg hover:from-purple-700 hover:to-blue-700 transition-all duration-300"
        >
          {t('user.saveSettings')}
        </button>
      </div>
    </div>
  );
};

export default UserProfile;
