import React, { useState } from 'react';
import { Share2, Download, Copy } from 'lucide-react';

const ShareButton = ({ zodiacSign, horoscope, rating }) => {
  const [showShareMenu, setShowShareMenu] = useState(false);
  const [copied, setCopied] = useState(false);

  const generateShareText = () => {
    return `🌟 今日${zodiacSign}运势 🌟\n\n${horoscope?.description || ''}\n\n综合运势：${'⭐'.repeat(rating || 0)}\n\n#星座运势 #${zodiacSign}`;
  };

  const generateShareImage = async () => {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    canvas.width = 400;
    canvas.height = 600;

    // 背景渐变
    const gradient = ctx.createLinearGradient(0, 0, 0, 600);
    gradient.addColorStop(0, '#8B5CF6');
    gradient.addColorStop(1, '#3B82F6');
    ctx.fillStyle = gradient;
    ctx.fillRect(0, 0, 400, 600);

    // 文字内容
    ctx.fillStyle = 'white';
    ctx.font = 'bold 24px Arial';
    ctx.textAlign = 'center';
    ctx.fillText(`${zodiacSign}今日运势`, 200, 80);
    
    ctx.font = '16px Arial';
    const text = horoscope?.description || '';
    const words = text.split('');
    let line = '';
    let y = 150;
    
    for (let i = 0; i < words.length; i++) {
      const testLine = line + words[i];
      if (ctx.measureText(testLine).width > 350 && i > 0) {
        ctx.fillText(line, 200, y);
        line = words[i];
        y += 25;
      } else {
        line = testLine;
      }
    }
    ctx.fillText(line, 200, y);

    // 星级评分
    ctx.font = '20px Arial';
    ctx.fillText(`综合运势：${'⭐'.repeat(rating || 0)}`, 200, y + 60);

    return canvas.toDataURL('image/png');
  };

  const handleCopyText = async () => {
    try {
      await navigator.clipboard.writeText(generateShareText());
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error('复制失败:', err);
    }
  };

  const handleDownloadImage = async () => {
    const imageData = await generateShareImage();
    const link = document.createElement('a');
    link.download = `${zodiacSign}-运势-${new Date().toLocaleDateString()}.png`;
    link.href = imageData;
    link.click();
  };

  const handleWebShare = async () => {
    if (navigator.share) {
      try {
        await navigator.share({
          title: `${zodiacSign}今日运势`,
          text: generateShareText(),
          url: window.location.href
        });
      } catch (err) {
        console.error('分享失败:', err);
      }
    } else {
      setShowShareMenu(true);
    }
  };

  return (
    <div className="relative">
      <button
        onClick={handleWebShare}
        className="flex items-center space-x-2 bg-gradient-to-r from-pink-500 to-purple-600 text-white px-4 py-2 rounded-lg hover:from-pink-600 hover:to-purple-700 transition-all duration-300"
      >
        <Share2 size={16} />
        <span>分享运势</span>
      </button>

      {showShareMenu && (
        <div className="absolute top-full mt-2 right-0 bg-white rounded-lg shadow-lg border p-2 z-10">
          <button
            onClick={handleCopyText}
            className="flex items-center space-x-2 w-full px-3 py-2 text-left hover:bg-gray-100 rounded"
          >
            <Copy size={16} />
            <span>{copied ? '已复制!' : '复制文字'}</span>
          </button>
          <button
            onClick={handleDownloadImage}
            className="flex items-center space-x-2 w-full px-3 py-2 text-left hover:bg-gray-100 rounded"
          >
            <Download size={16} />
            <span>下载图片</span>
          </button>
        </div>
      )}
    </div>
  );
};

export default ShareButton;
