import React from 'react';
import { useTranslation } from '../../hooks/useTranslation';

const Footer = () => {
  const { t } = useTranslation();
  const currentYear = new Date().getFullYear();

  return (
    <footer className="bg-gray-800 text-white py-8">
      <div className="container mx-auto px-4">
        <div className="grid md:grid-cols-3 gap-8">
          {/* 网站信息 */}
          <div>
            <h3 className="text-lg font-bold mb-4">{t('site.title')}</h3>
            <p className="text-gray-300 text-sm">
              {t('site.description')}
            </p>
          </div>

          {/* 快速链接 */}
          <div>
            <h4 className="font-semibold mb-4">{t('footer.quickLinks')}</h4>
            <ul className="space-y-2 text-sm">
              <li><a href="#" className="text-gray-300 hover:text-white">{t('navigation.home')}</a></li>
              <li><a href="#" className="text-gray-300 hover:text-white">{t('navigation.about')}</a></li>
              <li><a href="#" className="text-gray-300 hover:text-white">{t('footer.privacy')}</a></li>
              <li><a href="#" className="text-gray-300 hover:text-white">{t('footer.terms')}</a></li>
            </ul>
          </div>

          {/* 免责声明 */}
          <div>
            <h4 className="font-semibold mb-4">{t('footer.disclaimer')}</h4>
            <p className="text-gray-300 text-sm">
              {t('footer.disclaimerText')}
            </p>
          </div>
        </div>

        <div className="border-t border-gray-700 mt-8 pt-4 text-center text-sm text-gray-300">
          <p>&copy; {currentYear} {t('site.title')}. {t('footer.allRightsReserved')}</p>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
