import { useState, useEffect } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { SUPPORTED_LANGUAGES, DEFAULT_LANGUAGE } from '../utils/constants';

export const useLanguage = () => {
  const { lang } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const [currentLanguage, setCurrentLanguage] = useState(DEFAULT_LANGUAGE);

  useEffect(() => {
    // 验证URL中的语言参数
    if (lang && SUPPORTED_LANGUAGES[lang]) {
      setCurrentLanguage(lang);
      localStorage.setItem('preferred-language', lang);
    } else if (!lang) {
      // 检测浏览器语言或使用存储的偏好
      const savedLang = localStorage.getItem('preferred-language');
      const browserLang = navigator.language.split('-')[0];
      const detectedLang = SUPPORTED_LANGUAGES[savedLang] ? savedLang : 
                          SUPPORTED_LANGUAGES[browserLang] ? browserLang : 
                          DEFAULT_LANGUAGE;
      
      setCurrentLanguage(detectedLang);
      // 只有在根路径时才重定向
      if (location.pathname === '/') {
        navigate(`/${detectedLang}`, { replace: true });
      }
    }
  }, [lang, navigate, location.pathname]);

  const changeLanguage = (newLang) => {
    if (SUPPORTED_LANGUAGES[newLang]) {
      const pathParts = location.pathname.split('/');
      
      // 如果当前路径包含语言代码，替换它
      if (pathParts[1] && SUPPORTED_LANGUAGES[pathParts[1]]) {
        pathParts[1] = newLang;
      } else {
        // 如果没有语言代码，添加到开头
        pathParts.splice(1, 0, newLang);
      }
      
      const newPath = pathParts.join('/');
      navigate(newPath, { replace: true });
    }
  };

  return {
    currentLanguage,
    changeLanguage,
    supportedLanguages: SUPPORTED_LANGUAGES,
    defaultLanguage: DEFAULT_LANGUAGE
  };
};
