import React from 'react';
import { useTranslation } from '../../hooks/useTranslation';

const HoroscopeChart = ({ horoscope }) => {
  const { t } = useTranslation();
  
  const categories = [
    { key: 'overall', label: t('horoscope.categories.overall'), color: 'bg-purple-500' },
    { key: 'love', label: t('horoscope.categories.love'), color: 'bg-pink-500' },
    { key: 'career', label: t('horoscope.categories.career'), color: 'bg-blue-500' },
    { key: 'money', label: t('horoscope.categories.money'), color: 'bg-green-500' },
    { key: 'health', label: t('horoscope.categories.health'), color: 'bg-orange-500' }
  ];

  const RadarChart = () => {
    const size = 200;
    const center = size / 2;
    const radius = 70;
    const levels = 5;
    
    const angleStep = (2 * Math.PI) / categories.length;
    
    const getPoint = (value, index) => {
      const angle = index * angleStep - Math.PI / 2;
      const r = (value / 5) * radius;
      return {
        x: center + r * Math.cos(angle),
        y: center + r * Math.sin(angle)
      };
    };

    const dataPoints = categories.map((cat, index) => 
      getPoint(horoscope?.[cat.key] || 0, index)
    );

    const pathData = dataPoints.map((point, index) => 
      `${index === 0 ? 'M' : 'L'} ${point.x} ${point.y}`
    ).join(' ') + ' Z';

    return (
      <div className="bg-white rounded-xl shadow-lg p-6">
        <h3 className="text-lg font-bold text-center mb-4 text-gray-800">
          📊 {t('horoscope.radarChart')}
        </h3>
        <div className="flex justify-center">
          <svg width={size} height={size} className="overflow-visible">
            {/* 背景网格 */}
            {[...Array(levels)].map((_, i) => {
              const r = ((i + 1) / levels) * radius;
              const points = categories.map((_, index) => {
                const angle = index * angleStep - Math.PI / 2;
                return `${center + r * Math.cos(angle)},${center + r * Math.sin(angle)}`;
              }).join(' ');
              
              return (
                <polygon
                  key={i}
                  points={points}
                  fill="none"
                  stroke="#e5e7eb"
                  strokeWidth="1"
                />
              );
            })}
            
            {/* 轴线 */}
            {categories.map((_, index) => {
              const angle = index * angleStep - Math.PI / 2;
              return (
                <line
                  key={index}
                  x1={center}
                  y1={center}
                  x2={center + radius * Math.cos(angle)}
                  y2={center + radius * Math.sin(angle)}
                  stroke="#e5e7eb"
                  strokeWidth="1"
                />
              );
            })}
            
            {/* 数据区域 */}
            <path
              d={pathData}
              fill="rgba(139, 92, 246, 0.3)"
              stroke="#8B5CF6"
              strokeWidth="2"
            />
            
            {/* 数据点 */}
            {dataPoints.map((point, index) => (
              <circle
                key={index}
                cx={point.x}
                cy={point.y}
                r="4"
                fill="#8B5CF6"
              />
            ))}
            
            {/* 标签 */}
            {categories.map((cat, index) => {
              const angle = index * angleStep - Math.PI / 2;
              const labelRadius = radius + 20;
              const x = center + labelRadius * Math.cos(angle);
              const y = center + labelRadius * Math.sin(angle);
              
              return (
                <text
                  key={index}
                  x={x}
                  y={y}
                  textAnchor="middle"
                  dominantBaseline="middle"
                  className="text-xs font-medium fill-gray-600"
                >
                  {cat.label}
                </text>
              );
            })}
          </svg>
        </div>
      </div>
    );
  };

  const BarChart = () => (
    <div className="bg-white rounded-xl shadow-lg p-6">
      <h3 className="text-lg font-bold text-center mb-4 text-gray-800">
        📈 {t('horoscope.barChart')}
      </h3>
      <div className="space-y-3">
        {categories.map((cat) => {
          const value = horoscope?.[cat.key] || 0;
          const percentage = (value / 5) * 100;
          
          return (
            <div key={cat.key} className="flex items-center">
              <div className="w-12 text-sm font-medium text-gray-600">
                {cat.label}
              </div>
              <div className="flex-1 mx-3">
                <div className="bg-gray-200 rounded-full h-3 relative overflow-hidden">
                  <div
                    className={`h-full ${cat.color} rounded-full transition-all duration-1000 ease-out`}
                    style={{ width: `${percentage}%` }}
                  />
                </div>
              </div>
              <div className="w-8 text-sm font-bold text-gray-800">
                {value}/5
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );

  return (
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
      <RadarChart />
      <BarChart />
    </div>
  );
};

export default HoroscopeChart;
