import React from 'react';

const Footer = ({ content }) => {
  const currentYear = new Date().getFullYear();

  return (
    <footer className="bg-gray-800 text-white py-8">
      <div className="container mx-auto px-4">
        <div className="grid md:grid-cols-3 gap-8">
          {/* 网站信息 */}
          <div>
            <h3 className="text-lg font-bold mb-4">{content.siteTitle}</h3>
            <p className="text-gray-300 text-sm">
              {content.siteDescription}
            </p>
          </div>

          {/* 快速链接 */}
          <div>
            <h4 className="font-semibold mb-4">{content.quickLinks}</h4>
            <ul className="space-y-2 text-sm">
              <li><a href="#" className="text-gray-300 hover:text-white">{content.home}</a></li>
              <li><a href="#" className="text-gray-300 hover:text-white">{content.about}</a></li>
              <li><a href="#" className="text-gray-300 hover:text-white">{content.privacy}</a></li>
              <li><a href="#" className="text-gray-300 hover:text-white">{content.terms}</a></li>
            </ul>
          </div>

          {/* 免责声明 */}
          <div>
            <h4 className="font-semibold mb-4">{content.disclaimer}</h4>
            <p className="text-gray-300 text-sm">
              {content.disclaimerText}
            </p>
          </div>
        </div>

        <div className="border-t border-gray-700 mt-8 pt-4 text-center text-sm text-gray-300">
          <p>&copy; {currentYear} {content.siteTitle}. {content.allRightsReserved}</p>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
