import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import { useTheme } from '../../components/theme/ThemeProvider';
import Header from '../../components/common/Header';
import Footer from '../../components/common/Footer';
import ShareButton from '../../components/social/ShareButton';

function DailyHoroscopePage({ language = 'en' }) {
  const { year, month, day } = useParams();
  const [dailyData, setDailyData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { theme } = useTheme();

  useEffect(() => {
    const loadDailyData = async () => {
      try {
        setLoading(true);
        const dateStr = `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`;
        const response = await fetch(`/data/daily/${dateStr}.json`);
        
        if (!response.ok) {
          throw new Error('Data not found');
        }
        
        const data = await response.json();
        setDailyData(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    loadDailyData();
  }, [year, month, day]);

  if (loading) {
    return (
      <div className={`min-h-screen ${theme.background} flex items-center justify-center`}>
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-white mx-auto mb-4"></div>
          <p className="text-white">Loading daily horoscope...</p>
        </div>
      </div>
    );
  }

  if (error || !dailyData) {
    return (
      <div className={`min-h-screen ${theme.background} flex items-center justify-center`}>
        <div className="text-center text-white">
          <h2 className="text-2xl font-bold mb-4">Data Not Available</h2>
          <p className="mb-4">Sorry, horoscope data for this date is not available.</p>
          <Link 
            to={language === 'zh' ? '/zh' : '/'} 
            className="bg-white text-purple-600 px-6 py-2 rounded-full hover:bg-gray-100 transition-colors"
          >
            {language === 'zh' ? '返回首页' : 'Back to Home'}
          </Link>
        </div>
      </div>
    );
  }

  const content = dailyData[language] || dailyData.en;
  const currentUrl = `https://dailyhoroscopetoday.org${language === 'zh' ? '/zh' : ''}/daily-horoscope/${year}/${month}/${day}`;
  
  // 获取运势等级对应的颜色
  const getLevelColor = (level) => {
    switch (level) {
      case 'excellent': return 'from-green-400 to-emerald-500';
      case 'good': return 'from-blue-400 to-cyan-500';
      case 'average': return 'from-yellow-400 to-orange-500';
      case 'challenging': return 'from-red-400 to-pink-500';
      default: return 'from-gray-400 to-gray-500';
    }
  };

  const getLevelText = (level) => {
    const texts = {
      en: {
        excellent: 'Excellent',
        good: 'Good',
        average: 'Average',
        challenging: 'Challenging'
      },
      zh: {
        excellent: '极佳',
        good: '良好',
        average: '一般',
        challenging: '挑战'
      }
    };
    return texts[language][level] || level;
  };

  return (
    <div className={`min-h-screen ${theme.background}`}>
      <Helmet>
        <title>{content.title}</title>
        <meta name="description" content={content.description} />
        <meta name="keywords" content={content.keywords} />
        
        {/* Open Graph */}
        <meta property="og:title" content={content.title} />
        <meta property="og:description" content={content.description} />
        <meta property="og:url" content={currentUrl} />
        <meta property="og:type" content="article" />
        <meta property="og:site_name" content="Daily Horoscope Today" />
        
        {/* Twitter Card */}
        <meta name="twitter:card" content="summary_large_image" />
        <meta name="twitter:title" content={content.title} />
        <meta name="twitter:description" content={content.description} />
        
        {/* 多语言 hreflang */}
        <link rel="alternate" hrefLang="en" href={`https://dailyhoroscopetoday.org/daily-horoscope/${year}/${month}/${day}`} />
        <link rel="alternate" hrefLang="zh" href={`https://dailyhoroscopetoday.org/zh/daily-horoscope/${year}/${month}/${day}`} />
        <link rel="canonical" href={currentUrl} />
        
        {/* 结构化数据 */}
        <script type="application/ld+json">
          {JSON.stringify({
            "@context": "https://schema.org",
            "@type": "Article",
            "headline": content.title,
            "description": content.description,
            "datePublished": `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}T00:00:00Z`,
            "dateModified": `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}T00:00:00Z`,
            "author": {
              "@type": "Organization",
              "name": "Daily Horoscope Today"
            },
            "publisher": {
              "@type": "Organization",
              "name": "Daily Horoscope Today",
              "url": "https://dailyhoroscopetoday.org"
            },
            "mainEntityOfPage": {
              "@type": "WebPage",
              "@id": currentUrl
            },
            "inLanguage": language === 'zh' ? 'zh-CN' : 'en-US'
          })}
        </script>
      </Helmet>

      <Header />
      
      <main className="container mx-auto px-4 py-8">
        {/* 面包屑导航 */}
        <nav className="mb-8">
          <ol className="flex items-center space-x-2 text-sm text-white/80">
            <li>
              <Link to={language === 'zh' ? '/zh' : '/'} className="hover:text-white transition-colors">
                {language === 'zh' ? '首页' : 'Home'}
              </Link>
            </li>
            <li>/</li>
            <li>
              <span className="text-white">
                {language === 'zh' ? '每日运势' : 'Daily Horoscope'}
              </span>
            </li>
            <li>/</li>
            <li>
              <span className="text-white">
                {year}/{month}/{day}
              </span>
            </li>
          </ol>
        </nav>

        {/* 页面标题 */}
        <div className="text-center mb-12">
          <h1 className="text-4xl md:text-5xl font-bold text-white mb-4">
            {language === 'zh' ? '今日星座排行榜' : "Today's Zodiac Rankings"}
          </h1>
          <p className="text-xl text-white/90 mb-6">
            {year}/{month}/{day}
          </p>
          <p className="text-lg text-white/80 max-w-2xl mx-auto">
            {language === 'zh' 
              ? '发现你的星座今日运势，看看哪个星座今天运气最好！' 
              : 'Discover your zodiac sign\'s fortune for today and see which signs have the best luck!'}
          </p>
        </div>

        {/* 排行榜 */}
        <div className="max-w-4xl mx-auto">
          <div className="grid gap-6">
            {content.rankings.map((zodiac, index) => (
              <div 
                key={zodiac.id}
                className={`${theme.card} backdrop-blur-lg rounded-2xl p-6 transform hover:scale-105 transition-all duration-300 hover:shadow-2xl`}
              >
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-4">
                    {/* 排名 */}
                    <div className={`w-12 h-12 rounded-full bg-gradient-to-r ${getLevelColor(zodiac.level)} flex items-center justify-center text-white font-bold text-lg`}>
                      #{zodiac.rank}
                    </div>
                    
                    {/* 星座信息 */}
                    <div>
                      <div className="flex items-center space-x-2">
                        <span className="text-2xl">{zodiac.emoji}</span>
                        <h3 className="text-xl font-bold text-white">
                          {zodiac.name[language]}
                        </h3>
                      </div>
                      <p className="text-white/80 mt-1">
                        {zodiac.description}
                      </p>
                    </div>
                  </div>
                  
                  {/* 分数和等级 */}
                  <div className="text-right">
                    <div className="text-2xl font-bold text-white mb-1">
                      {zodiac.score}
                    </div>
                    <div className={`px-3 py-1 rounded-full text-sm font-medium bg-gradient-to-r ${getLevelColor(zodiac.level)} text-white`}>
                      {getLevelText(zodiac.level)}
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* 分享按钮 */}
        <div className="text-center mt-12">
          <ShareButton 
            url={currentUrl}
            title={content.title}
            description={content.description}
          />
        </div>

        {/* 导航链接 */}
        <div className="flex justify-center space-x-4 mt-8">
          <Link 
            to={language === 'zh' ? '/zh' : '/'}
            className="bg-white/20 backdrop-blur-lg text-white px-6 py-3 rounded-full hover:bg-white/30 transition-all duration-300"
          >
            {language === 'zh' ? '返回首页' : 'Back to Home'}
          </Link>
          <Link 
            to={language === 'zh' ? '/zh/horoscope' : '/horoscope'}
            className="bg-white/20 backdrop-blur-lg text-white px-6 py-3 rounded-full hover:bg-white/30 transition-all duration-300"
          >
            {language === 'zh' ? '查看详细运势' : 'View Detailed Horoscope'}
          </Link>
        </div>
      </main>

      <Footer />
    </div>
  );
}

export default DailyHoroscopePage;
