import React, { useState } from 'react';
import { Share2, Download, Copy } from 'lucide-react';

const ShareButton = ({ zodiacSign, horoscope, rating, content }) => {
  const [showShareMenu, setShowShareMenu] = useState(false);
  const [copied, setCopied] = useState(false);

  const generateShareText = () => {
    return `üåü ${content.todayHoroscope.replace('{zodiac}', content.zodiacNames[zodiacSign])} üåü\n\n${horoscope?.description || ''}\n\n${content.overallRating}Ôºö${'‚≠ê'.repeat(rating || 0)}\n\n${content.hashtags}`;
  };

  const generateShareImage = async () => {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');
    canvas.width = 400;
    canvas.height = 600;

    // ËÉåÊôØÊ∏êÂèò
    const gradient = ctx.createLinearGradient(0, 0, 0, 600);
    gradient.addColorStop(0, '#8B5CF6');
    gradient.addColorStop(1, '#3B82F6');
    ctx.fillStyle = gradient;
    ctx.fillRect(0, 0, 400, 600);

    // ÊñáÂ≠óÂÜÖÂÆπ
    ctx.fillStyle = 'white';
    ctx.font = 'bold 24px Arial';
    ctx.textAlign = 'center';
    ctx.fillText(`${content.zodiacNames[zodiacSign]}${content.todayFortune}`, 200, 80);
    
    ctx.font = '16px Arial';
    const text = horoscope?.description || '';
    const words = text.split('');
    let line = '';
    let y = 150;
    
    for (let i = 0; i < words.length; i++) {
      const testLine = line + words[i];
      if (ctx.measureText(testLine).width > 350 && i > 0) {
        ctx.fillText(line, 200, y);
        line = words[i];
        y += 25;
      } else {
        line = testLine;
      }
    }
    ctx.fillText(line, 200, y);

    // ÊòüÁ∫ßËØÑÂàÜ
    ctx.font = '20px Arial';
    ctx.fillText(`${content.overallRating}Ôºö${'‚≠ê'.repeat(rating || 0)}`, 200, y + 60);

    return canvas.toDataURL('image/png');
  };

  const handleCopyText = async () => {
    try {
      await navigator.clipboard.writeText(generateShareText());
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    } catch (err) {
      console.error(content.copyFailed, err);
    }
  };

  const handleDownloadImage = async () => {
    const imageData = await generateShareImage();
    const link = document.createElement('a');
    link.download = `${content.zodiacNames[zodiacSign]}-${content.horoscope}.png`;
    link.href = imageData;
    link.click();
  };

  const handleShare = async (platform) => {
    const text = generateShareText();
    const url = window.location.href;
    
    switch (platform) {
      case 'twitter':
        window.open(`https://twitter.com/intent/tweet?text=${encodeURIComponent(text)}&url=${encodeURIComponent(url)}`);
        break;
      case 'facebook':
        window.open(`https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(url)}`);
        break;
      case 'whatsapp':
        window.open(`https://wa.me/?text=${encodeURIComponent(text + ' ' + url)}`);
        break;
      case 'telegram':
        window.open(`https://t.me/share/url?url=${encodeURIComponent(url)}&text=${encodeURIComponent(text)}`);
        break;
      default:
        if (navigator.share) {
          try {
            await navigator.share({
              title: content.shareTitle,
              text: text,
              url: url
            });
          } catch (err) {
            console.error(content.shareFailed, err);
          }
        }
    }
    setShowShareMenu(false);
  };

  return (
    <div className="relative">
      <button
        onClick={() => setShowShareMenu(!showShareMenu)}
        className="bg-gradient-to-r from-purple-600 to-pink-600 text-white px-4 py-2 rounded-lg flex items-center space-x-2 hover:from-purple-700 hover:to-pink-700 transition-all duration-200"
      >
        <Share2 className="w-4 h-4" />
        <span>{content.share}</span>
      </button>

      {showShareMenu && (
        <div className="absolute top-full mt-2 right-0 bg-white rounded-lg shadow-xl border p-4 z-50 min-w-48">
          <div className="space-y-2">
            <button
              onClick={handleCopyText}
              className="w-full flex items-center space-x-2 px-3 py-2 text-left hover:bg-gray-100 rounded transition-colors"
            >
              <Copy className="w-4 h-4" />
              <span>{copied ? content.copied : content.copyText}</span>
            </button>
            
            <button
              onClick={handleDownloadImage}
              className="w-full flex items-center space-x-2 px-3 py-2 text-left hover:bg-gray-100 rounded transition-colors"
            >
              <Download className="w-4 h-4" />
              <span>{content.downloadImage}</span>
            </button>
            
            <hr className="my-2" />
            
            <button
              onClick={() => handleShare('twitter')}
              className="w-full flex items-center space-x-2 px-3 py-2 text-left hover:bg-gray-100 rounded transition-colors"
            >
              <span>üê¶</span>
              <span>Twitter</span>
            </button>
            
            <button
              onClick={() => handleShare('facebook')}
              className="w-full flex items-center space-x-2 px-3 py-2 text-left hover:bg-gray-100 rounded transition-colors"
            >
              <span>üìò</span>
              <span>Facebook</span>
            </button>
            
            <button
              onClick={() => handleShare('whatsapp')}
              className="w-full flex items-center space-x-2 px-3 py-2 text-left hover:bg-gray-100 rounded transition-colors"
            >
              <span>üí¨</span>
              <span>WhatsApp</span>
            </button>
            
            <button
              onClick={() => handleShare('telegram')}
              className="w-full flex items-center space-x-2 px-3 py-2 text-left hover:bg-gray-100 rounded transition-colors"
            >
              <span>‚úàÔ∏è</span>
              <span>Telegram</span>
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ShareButton;
